import { Button, EmptyState } from '@shopify/polaris'
import React, { useEffect, useState } from 'react'

// Here I am using Shopify API
function Form() {

  const url = 'https://cors-anywhere.herokuapp.com/https://embedded-game.myshopify.com/admin/api/2020-10/draft_orders.json'

  const requestOptions = {
    method: 'POST',
    withCredentials: true,
    headers: {
      'Content-Type': 'application/json',
      'SHOPIFY_API_KEY': 'ae3cb5e86bd8d57567291d56426f12a8',
      'SHOPIFY_API_SECRET_KEY': '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'
      // "crossDomain": true,
      // 'Access-Control-Allow-Origin': '*',
    },
    body: JSON.stringify({
      "draft_order": {
        "line_items": [
          {
            "custom": false,
            "title": "Custom Product",
            "product_id": 4008421883976,
            "price": "1000.00",
            "properties": [
              {
                "name": "custom engraving",
                "value": "Happy Birthday Mom!"
              }
            ],
            "quantity": 5
          }
        ],
        "customer": {
          "id": 1000
        },
        "email": "abhisheksrivastavabbn@gmail.com"
      }
    })
  };

  const createOrder = (e) => {
    e.preventDefault();
    console.log("Order Create Button clicked")
    fetch(url, requestOptions)
      .then(
        console.log("Order Created!!")
      )
      .catch((err) => {
        console.log(err)
      })
  }

  return (
    <EmptyState
      heading="Create Order"
    >
      <form onSubmit={createOrder}>
        <button type="submit">Create a new Draft Order</button>
      </form>
    </EmptyState>
  )
}

export default Form
