import { Button, EmptyState } from '@shopify/polaris'
import React, { useEffect, useState } from 'react'

// Here I am using Shopify API
function Form() {

  const url = 'https://cors-anywhere.herokuapp.com/https://embedded-game.myshopify.com/admin/api/2020-10/draft_orders.json'

  const requestOptions = {
    method: 'POST',
    withCredentials: true,
    headers: {
      'Content-Type': 'application/json',
      'X-Shopify-Access-Token': 'ae3cb5e86bd8d57567291d56426f12a8',
      'X-Requested-With': 'XMLHttpRequest'
    },
    body: JSON.stringify({
      "draft_order": {
        "line_items": [
          {
            "title": "Custom Tee",
            "price": "20.00",
            "quantity": 2
          }
        ],
        "applied_discount": {
          "description": "Custom discount",
          "value_type": "fixed_amount",
          "value": "10.0",
          "amount": "10.00",
          "title": "Custom"
        },
        "customer": {
          "id": 4434849562811
        },
        "use_customer_default_address": true
      }
    })
  };

  const createOrder = (e) => {
    e.preventDefault();
    console.log("Order Create Button clicked")
    fetch(url, requestOptions)
      .then( (res) => {
        console.log(res)
        console.log(res.data)
      })
      .catch((err) => {
        console.log(err)
      })
  }

  return (
    <EmptyState
      heading="Create Order"
    >
      <form onSubmit={createOrder}>
        <button type="submit">Create a new Draft Order</button>
      </form>
    </EmptyState>
  )
}

export default Form
