import { Card, EmptyState, Layout, OptionList, Scrollable } from '@shopify/polaris';
import { gql } from 'apollo-boost';
import React, { useEffect, useState } from 'react'
import { Query } from 'react-apollo';
import Form from './Form';
import ReviewForm from './ReviewForm';

/*

* Getting List of existing Customers -> Shopify API - here (not done)

*/

const GET_CUSTOMERS = gql`
    query getCustomers {
      customers {
        edges {
          node {
            displayName
            totalSpent
          }
        }
      }
    }
`


class CustomerList extends React.Component {
  render() {
    return (
      <Query query={GET_CUSTOMERS}>
        {({ data, loading, error }) => {
          if (loading) { return <div>Loadingâ€¦</div>; }
          if (error) { return <div>{error.message}</div>; }

          {
            return data.customers.edges.map((edge) => (
              <p key={edge.node.id}> {edge.node.displayName}</p>
            ))
          }
        }}
      </Query>
    )
  }
}


