import { Card, EmptyState, Layout, OptionList, Scrollable } from '@shopify/polaris';
import { gql } from 'apollo-boost';
import React, { useEffect, useState } from 'react'
import { Query } from 'react-apollo';
import Form from './Form';
import ReviewForm from './ReviewForm';

/*

* Getting List of existing Customers -> Shopify API - here (done)

*/


const GET_CUSTOMERS = gql`
query getCustomers {
    customers (first: 3) {
      edges {
        node {
          id
          displayName
          email
        }
      }
    }
  }
`

function CustomerList() {
  const [selected, setSelected] = useState([]);

  useEffect(() => {
    console.log(selected)
  }, [selected])


  return (
    <Query query={GET_CUSTOMERS}>
      {({ loading, error, data }) => {
        if (loading) return <p>Loading...</p>;
        if (error) return <p>{error.message}</p>;

        console.log("Here's the ENDGAME \n", data)

        return (
          <Card>
            <ResourceList
              showHeader
              resourceName={{ singular: "Customer", plural: "Customers" }}
              items={data.nodes}
              renderItem={(item) => {
                const media = (
                  <Thumbnail
                    source={
                      item.images.edges[0]
                        ? item.images.edges[0].node.originalSrc
                        : ""
                    }
                    alt={
                      item.images.edges[0]
                        ? item.images.edges[0].node.altText
                        : ""
                    }
                  />
                );
                const price = item.variants.edges[0].node.price;
                return (
                  <ResourceList.Item
                    id={item.id}
                    media={media}
                    accessibilityLabel={`View details for ${item.title}`}
                    onClick={() => {
                      store.set("item", item);
                      redirectToProduct();
                    }}
                  >
                    <Stack>
                      <Stack.Item fill>
                        <h3>
                          <TextStyle variation="strong">
                            {item.title}
                          </TextStyle>
                        </h3>
                      </Stack.Item>
                      <Stack.Item>
                        <p>${price}</p>
                      </Stack.Item>
                      <Stack.Item>
                        <p>Expires on {twoWeeksFromNow} </p>
                      </Stack.Item>
                    </Stack>
                  </ResourceList.Item>
                );
              }}
            />
          </Card>
        );

        // return data.customers.edges.map(edge => (
        //   <div key={edge.node.id}>
        //     <p>{`${edge.node.displayName} by ${edge.node.email}`}</p>
        //   </div>
        // ));

        // return (
        //   <Layout >
        //     <Layout.Section oneHalf>
        //       <EmptyState>
        //         <ReviewForm customers={selected} setCustomers={setSelected} />
        //       </EmptyState>
        //     </Layout.Section>

        //     <Layout.Section oneHalf>

        //       <Card>
        //         <Scrollable shadow style={{ height: '300px' }}>
        //           <OptionList
        //             title="To be added among customers"
        //             onChange={setSelected}
        //             allowMultiple

        //             // options={[
        //             //   { 
        //             //     label: 'BBN Frank', 
        //             //     value: 'abhisheksrivastavabbn@gmail.com' 
        //             //   },
        //             //   { 
        //             //     label: 'Dale Frank', 
        //             //     value: 'abhisheksrivastavabbn@gmail.com' 
        //             //   }
        //             // ]}
        //             selected={selected}
        //           />
        //         </Scrollable>
        //       </Card>
        //     </Layout.Section>
        //   </Layout>
        // )
      }}
    </Query>
  );
}

export default CustomerList
