  return (
    <EmptyState>
      <Card>
        <OptionList
          title="Inventory Location"
          onChange={setSelected}
          allowMultiple
          options={[
            { id: '1', email: 'abhisheksrivastavabbn@gmail.com' },
            { id: '1', email: 'abhisheksrivastavasept5@gmail.com' },
            { id: '1', email: 'abhisheksrivastava29sept@gmail.com' },
            { id: '1', email: 'abhishek.srivastava2019@vitbhopal.ac.in' },
            { id: '1', email: 'abhishek.srivastava@surveysparrow.com' },
          ]}
          selected={selected}
        />
      </Card>
      <Form customers={selected}/>
    </EmptyState>
  );
}
export default CustomerList
