import { Card, EmptyState, Layout, OptionList, Scrollable } from '@shopify/polaris';
import { gql } from 'apollo-boost';
import React, { useEffect, useState } from 'react'
import { Query } from 'react-apollo';
import Form from './Form';
import ReviewForm from './ReviewForm';
import {
  ResourceList,
  Stack,
  TextStyle,
} from "@shopify/polaris";

/*

* Getting List of existing Customers -> Shopify API - here (done)

*/


const GET_CUSTOMERS = gql`
query getCustomers {
    customers (first: 3) {
      edges {
        node {
          id
          displayName
          email
        }
      }
    }
  }
`

function CustomerList() {
  const [selected, setSelected] = useState([]);

  useEffect(() => {
    console.log(selected)
  }, [selected])


  return (
    <Query query={GET_CUSTOMERS}>
      {({ loading, error, data }) => {
        if (loading) return <p>Loading...</p>;
        if (error) return <p>{error.message}</p>;

        console.log("Here's the ENDGAME \n", data)

        return (
          <Card>
            <ResourceList
              showHeader
              resourceName={{ singular: "Customer", plural: "Customers" }}
              items={data.customers.edges.node}
              renderItem={(item) => {
                return (
                  <ResourceList.Item
                    id={item.id}
                    accessibilityLabel={`View details for ${item.displayName}`}
                  >
                    <Stack>
                      <Stack.Item fill>
                        <h3>
                          <TextStyle variation="strong">
                            {item.displayName}
                          </TextStyle>
                        </h3>
                      </Stack.Item>
                      <Stack.Item>
                        <p>{email}</p>
                      </Stack.Item>
                    </Stack>
                  </ResourceList.Item>
                );
              }}
            />
          </Card>
        );
      }}
    </Query>
  );
}

export default CustomerList
