import { Button, Card, EmptyState, Layout, OptionList, Scrollable, TextField } from '@shopify/polaris'
import React, { useCallback, useEffect, useState } from 'react'

/*
Things I am doing here:

* Sending an invitation to be among customers -> SurveySparrow API
* Fetching a list of people who filled a survey -> SurveySparrow API
* Adding those people among my shopify Customers -> Shopify API
* After adding a customer, deleting their response -> SurveySparrow API

*/

function InviteCustomer() {
  const [email, setEmail] = useState('');
  const handleChange = useCallback((newValue) => setEmail(newValue), []);

  const [selected, setSelected] = useState([]);
  useEffect(() => {
    console.log(selected)
  }, [selected])

  const url = 'https://api.surveysparrow.com/v1/shares/email/2244557'
  const bearer = 'Bearer ' + 'pr0XQuLJrxoeXIiJ6fhDnp_pJ3QgHQ2-8jY2xTrjbfLx1Iv-iFn6KxNtGi01SRDDFWJUmDpNS8SnawQl97OGIMkA';
  const requestOptions = {
    method: 'PUT',
    withCredentials: true,
    headers: {
      'Content-Type': 'application/json',
      'Authorization': bearer,
    },
    body: JSON.stringify({
      "contacts": [email]
    })
  };

  const sendInvite = (e) => {

    e.preventDefault();
    console.log("Sending Customer Invite Mail...")

    if (email) {
      console.log(email)

      fetch(url, requestOptions)
        .then((res) => {
          console.log("Customer Invite Mail Sent!")
        })
        .catch((err) => {
          console.log(err)
        })

      setEmail('');
    }
    else {
      console.log("You entered any email id")
    }
  };

  return (
    <Layout >
      <EmptyState
        heading="Add a new Customer"
      >
        <form onSubmit={sendInvite}>
          <TextField
            label="Email id"
            value={email}
            onChange={handleChange}
            placeholder="Enter email id"
          />
          <Button submit primary>Share Survey</Button>
        </form>
      </EmptyState>

      <Layout.Section oneHalf>
        <EmptyState>
          <Card>
            <Scrollable shadow style={{ height: '300px' }}>
              <OptionList
                title="Customers"
                onChange={setSelected}
                allowMultiple
                options={[
                  { label: 'BBN Frank', value: 'abhisheksrivastavabbn@gmail.com' },
                  { label: 'Sept Fifth', value: 'abhisheksrivastavasept5@gmail.com' },
                  { label: 'Londer Sept', value: 'abhisheksrivastava29sept@gmail.com' },
                  { label: 'V technology', value: 'abhishek.srivastava2019@vitbhopal.ac.in' },
                  { label: 'Survey Player', value: 'abhishek.srivastava@surveysparrow.com' },
                ]}
                selected={selected}
              />
            </Scrollable>
          </Card>
        </EmptyState>
      </Layout.Section>
      {/* <Layout.Section oneHalf>
        <Form customers={selected} setCustomers={setSelected} />
        <ReviewForm customers={selected} setCustomers={setSelected} />
      </Layout.Section> */}
    </Layout>
  )
}

export default InviteCustomer
