import { Card, EmptyState, Form, OptionList } from '@shopify/polaris';
import React, { useEffect, useState } from 'react'

function CustomerList() {
  const [selected, setSelected] = useState([]);

  useEffect(() => {
    console.log(selected)
  }, [selected])

  return (
    <EmptyState>
      <Card>
        <OptionList
          title="Inventory Location"
          onChange={setSelected}
          allowMultiple
          options={[
            { label: 'BBN', value: 'abhisheksrivastavabbn@gmail.com' },
            { label: 'Sept 5', value: 'abhisheksrivastavasept5@gmail.com' },
            { label: '29 Sept', value: 'abhisheksrivastava29sept@gmail.com' },
            { label: 'VIT Bhopal', value: 'abhishek.srivastava2019@vitbhopal.ac.in' },
            { label: 'Surveysparrow', value: 'abhishek.srivastava@surveysparrow.com' },
          ]}
          selected={selected}
        />
      </Card>
      <Form customers={selected}/>
    </EmptyState>
  );
}
export default CustomerList
