import React from 'react'
import { useState } from 'react'

function Form() {
    const [input, setInput] = useState("")
    const [email, setemail] = useState("")

    const url = 'https://api.surveysparrow.com/v1/shares/email/2233677'
    const bearer = 'Bearer ' + prUJqEHlpzWrjBGP477dWklUz2sP6YlxMb4x0tY4J1rgW59TIJei20mhE2bpSvmnt56tG9N5xRMBmwMYDSkVQ4NQ;

    useEffect(() => {
        const requestOptions = {
            method: 'PUT',
            // withCredentials: true,
            headers: { 
                'Content-Type': 'application/json',
                'Authorization': bearer,
            },
            body: JSON.stringify({
                    "contacts": ["abhisheksrivastavabbn@gmail.com"]                
            })
        };
        fetch(url, requestOptions)
            .then(
                console.log("sent mail")
            )

    }, [email]);


    const sendSurveys = (e) => {
        e.preventDefault();
        setemail(input);
        // console.log(email)
        setInput("");
    }

    return (
        <form onSubmit={sendSurveys}>
            <label for="name">Name</label>
            <input
                type="input"
                name="input"
                id="input"
                value={input}
                onChange={(e) => setInput(e.target.value)}
            />
            <button type="submit">Submit</button>
        </form>
    )
}

export default Form
