import { Button, EmptyState, TextField } from '@shopify/polaris'
import React, { useCallback, useState } from 'react'

/*
Things I am doing here:

* Sending an invitation to be among customers -> SurveySparrow API
* Fetching a list of people who filled a survey -> SurveySparrow API
* Adding those people among my shopify Customers -> Shopify API
* After adding a customer, deleting their response -> SurveySparrow API

*/

function InviteCustomer() {
  const [email, setEmail] = useState('');
  const handleChange = useCallback((newValue) => setEmail(newValue), []);

  const url = 'https://api.surveysparrow.com/v1/shares/email/2244557'
  const bearer = 'Bearer ' + 'pr0XQuLJrxoeXIiJ6fhDnp_pJ3QgHQ2-8jY2xTrjbfLx1Iv-iFn6KxNtGi01SRDDFWJUmDpNS8SnawQl97OGIMkA';
  const requestOptions = {
    method: 'PUT',
    withCredentials: true,
    headers: {
      'Content-Type': 'application/json',
      'Authorization': bearer,
    },
    body: JSON.stringify({
      "contacts": [email]
    })
  };

  const sendInvite = (e) => {

    e.preventDefault();
    console.log("Sending Customer Invite Mail...")

    if (email) {
      console.log(email)

      fetch(url, requestOptions)
        .then((res) => {
          console.log("Customer Invite Mail Sent!")
        })
        .catch((err) => {
          console.log(err)
        })

      setEmail('');
    }
    else {
      console.log("You entered any email id")
    }
  };

  return (
    <EmptyState
      heading="Add a new Customer"
    >
      <form onSubmit={sendInvite}>
        <TextField
        label="Email id" 
        value={email} 
        onChange={handleChange}
        placeholder="Enter email id"
        />
        <Button submit primary>Share Survey</Button>
      </form>
    </EmptyState>
  )
}

export default InviteCustomer
