import { Button, EmptyState, TextField } from '@shopify/polaris'
import React, { useCallback, useEffect } from 'react'
import { useState } from 'react'

// Here I am using surveySparrow API
function Form({ customers, setCustomers }) {
  // const [input, setInput] = useState("")
  // const [email, setemail] = useState([])

  const url = 'https://api.surveysparrow.com/v1/shares/email/2238828'
  const bearer = 'Bearer ' + 'pr0XQuLJrxoeXIiJ6fhDnp_pJ3QgHQ2-8jY2xTrjbfLx1Iv-iFn6KxNtGi01SRDDFWJUmDpNS8SnawQl97OGIMkA';

  // const customersEmail = customers.map((customer) =>
  //   <li>{customer.value}</li>
  // );

  const sendMail = () => {
    const requestOptions = {
      method: 'PUT',
      withCredentials: true,
      headers: {
        'Content-Type': 'application/json',
        'Authorization': bearer,
      },
      // cutomersEmail = extractEmail(customers)
      body: JSON.stringify({
        "contacts": customers
      })
    };
    fetch(url, requestOptions)
      .then((res) => {
        console.log("Order making form sent")
      })
      .catch((err) => {
        console.log(err)
      })
  };

  const sendSurveys = (e) => {
    e.preventDefault();
    console.log("Button clicked")
    if (customers) {
      // setemail(customers);
      console.log(customers)
      setCustomers([]);
    }
  }

  // const handleChange = useCallback((newValue) => setInput(newValue), []);

  return (
    <EmptyState >
      <form onSubmit={sendMail}>
        <label for="name">Share Survey through emails</label>
        {/* <TextField label="Email id" value={input} onChange={handleChange} /> */}
        <Button submit primary>Submit</Button>
      </form>
    </EmptyState>
  )
}

export default Form;
